import React, { Component } from 'react';
import '../../src/css/Login.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import md5 from 'md5';
import Cookies from 'universal-cookie';

const baseUrl = "http://localhost:3001/usuario";
const cookies = new Cookies();
const rolCargo = new rolCargo;

class Login extends Component {

    state={
        form:{
            username: '',
            password: ''
        }
    }

    handleChange=async e=>{
        this.setState({
            form:{
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
        // console.log(this.state.form);
    }


    iniciarSesion=async()=>{
        await axios.get(baseUrl, {params:{email: this.state.form.username , contraseña: md5(this.state.form.password) , rol: rolCargo }})
        .then(response=>{
            console.log(" response data"+ response.data);
            console.log(" rolCargo"+ rolCargo);
            return response.data;
        })     
        .then(response=>{
            if(response.length>0){
                var respuesta=response[0];
                cookies.set('id' , respuesta.id, {path:"/"})
                cookies.set('email' , respuesta.email, {path:"/"})
                cookies.set('nombre' , respuesta.nombre, {path:"/"})
                cookies.set('apellido' , respuesta.apellido, {path:"/"})
                cookies.set('rol' , respuesta.rol, {path:"/"})
                alert(`Bienvenido ${respuesta.nombre} ${respuesta.apellido}`)
                window.location.href="./menu";
            }else {
                alert('El usuario o la contraseña no son correctos.')
            }
        })
        // .then(response=>{
        //     var respuesta=response[0];
        //     if (response.length>0 && respuesta.rol === 1){
        //         window.location.href="./menuAdmin";
        //     } else if (response.length>0 && respuesta.rol === 2){
        //         window.location.href="./menu";
        //     }
        // })
        .catch(error=>{
            console.log(error);
            // alert("No se pudo inciar sesion");
        })
  
    }


    componentDidMount(){
        if(cookies.get('nombre')){
            window.location.href="./menu"
        }
    }

    render() {
        return (
           <div className="containerPrincipal">
               <div className="containerSecundario">
                   <div className="form-group">
                       <label>Usuario: </label>
                       <br/>
                            <input
                            type="text"
                            className="form-control"
                            name="username"
                            onChange={this.handleChange}
                            />
                        <br/>
                        <label>Contraseña</label>
                        <br/>
                        <input
                        type="password"
                        className="form-control"
                        name="password"
                        onChange={this.handleChange}
                        />
                        <br/>
                        <button className="btn  btn-primary" onClick={()=> this.iniciarSesion()} >Iniciar Sesión</button>
                   </div>
               </div>

           </div>
        );
    }
}

export default Login;
