import React, { Component } from 'react';
import Cookies from 'universal-cookie';
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';
const url = "http://localhost:62856/api/USUARIOs";
const cookies = new Cookies();

class MenuAdmin extends Component {

    state={
        data:[],
        modalInsertar: false,
    }

    peticionGet=()=>{
        axios.get(url).then(response=>{
            console.log("DATA USUARIOS" , response.data)
            this.setState({data: response.data});
        })
    }
    
    modalInsertar=()=>{
        this.setState({modalInsertar: !this.state.modalInsertar});
    }

    componentDidMount(){
        // if(!cookies.get('nombre')){
        //     window.location.href="./"
        // }
        this.peticionGet();
    }


    cerrarSesion=()=>{
        cookies.remove('id',{path:"/"});
        cookies.remove('nombre',{path:"/"});
        cookies.remove('apellido',{path:"/"});
        cookies.remove('rol',{path:"/"});
        window.location.href='./'
    }

    render() {
        return (

        <body>
            
            <header>
                 <div>
                     <button onClick={()=>this.cerrarSesion()}>Cerrar sesión</button>
                </div>
            </header>

            <div className="App">
                <br/>
                <button className="btn btn-success" onClick={()=>this.modalInsertar()}>Agregar Usuario</button>
                <br/>  <br/>
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Nombre</th>
                            <th>Apellido</th>
                            <th>Email</th>
                            <th>Contraseña</th>
                            <th>Direccion</th>
                            <th>Telefono</th>
                            <th>Estado</th>
                            <th>Rol</th>

                        </tr>
                    </thead>
                    <tbody>
                       {this.state.data.map(usuario=>{
                            return(
                                <tr>
                                    <td>{usuario.ID}</td>
                                    <td>{usuario.NOMBRE}</td>
                                    <td>{usuario.APELLIDO}</td>
                                    <td>{usuario.EMAIL}</td>
                                    <td>{usuario.CONTRASENA}</td>
                                    <td>{usuario.DIRECCION}</td>
                                    <td>{usuario.TELEFONO}</td>
                                    <td>{usuario.ACTIVO}</td>
                                    <td>{usuario.ROLID}</td>
                                    <td>
                                        <button className="btn btn-primary"> <FontAwesomeIcon icon={faEdit}/></button>
                                        {"    "}
                                        <button className="btn btn-primary"> <FontAwesomeIcon icon={faTrashAlt}/></button>
                                    </td>
                                </tr>
                            )
                       })}
                    </tbody>
                </table>

                    <Modal isOpen = {this.state.modalInsertar}>
                            <ModalHeader style={{display:'block'}}>
                                        <span style={{float: 'right'}}>x</span>
                            </ModalHeader>
                            <ModalBody>
                                <div className="form-group">
                                    <label htmlFor="ID">ID</label>
                                    <input className="form-control" type="text" name="id" id="id" readOnly></input>
                                    <br/>
                                    <label htmlFor="NOMBRE">Nombre</label>
                                    <input className="form-control" type="text" name="NOMBRE" id="NOMBRE"></input>
                                    <br/>
                                    <label htmlFor="APELLIDO">Apellido</label>
                                    <input className="form-control" type="text" name="APELLIDO" id="APELLIDO"></input>
                                    <br/>
                                    <label htmlFor="EMAIL">Email</label>
                                    <input className="form-control" type="text" name="EMAIL" id="EMAIL"></input>
                                    <br/>
                                    <label htmlFor="DIRECCION">Direccion</label>
                                    <input className="form-control" type="text" name="DIRECCION" id="DIRECCION"></input>
                                    <br/>
                                    <label htmlFor="TELEFONO">Telefono</label>
                                    <input className="form-control" type="text" name="TELEFONO" id="TELEFONO"></input>
                                    <br/>                       
                                    <label htmlFor="ROLID">Rol ID</label>
                                    <input className="form-control" type="text" name="ROLID" id="ROLID"></input>
                                </div>
                            </ModalBody>

                            <ModalFooter>
                                <button className= "btn btn-success">
                                    Insertar
                                </button>
                                <button className= "btn btn-danger" onClick={()=>this.modalInsertar()}>
                                    Cancelar
                                </button>
                            </ModalFooter>


                    </Modal>
            </div>
         </body>
  

        
       


        );
    }
}

export default MenuAdmin;
