import React, { Component } from 'react';
import '../../src/css/Login.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import md5 from 'md5';

const baseUrl = "http://localhost:3001/usuario";

class Login extends Component {

    state={
        form:{
            username: '',
            password: ''
        }
    }

    handleChange=async e=>{
        this.setState({
            form:{
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
        // console.log(this.state.form);
    }


    iniciarSesion=async()=>{
        await axios.get(baseUrl, {params:{email: this.state.form.username , contraseña: md5(this.state.form.password)}})
        .then(response=>{
            alert("Se inicio sesion correctamente diego qlo eri muy weno");
            console.log(response.data);
        })
        .catch(error=>{
            console.log(error);
            alert("No se pudo inciar sesion");
        })

        
    }


    render() {
        return (
           <div className="containerPrincipal">
               <div className="containerSecundario">
                   <div className="form-group">
                       <label>Usuario: </label>
                       <br/>
                            <input
                            type="text"
                            className="form-control"
                            name="username"
                            onChange={this.handleChange}
                            />
                        <br/>
                        <label>Contraseña</label>
                        <br/>
                        <input
                        type="password"
                        className="form-control"
                        name="password"
                        onChange={this.handleChange}
                        />
                        <br/>
                        <button className="btn  btn-primary" onClick={()=> this.iniciarSesion()} >Iniciar Sesión</button>
                   </div>
               </div>

           </div>
        );
    }
}

export default Login;
